    'hi' => '',
    'pa' => ''
];

// Save each flag
foreach ($flags as $code => $base64) {
    $file = "assets/flags/{$code}.png";
    file_put_contents($file, base64_decode($base64));
    echo "Created {$file}\n";
}

echo "All flags created successfully!\n";
?> 